import React from 'react';
import logo from './logo.svg';
import './App.css';

function App1() {
  return (
    <div className="App">
      <header className="App-header">
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

function App() {
  return React.createElement(
    "div",
    {},
    React.createElement(
      "header",
      {
        className: "App-header"
      },
      React.createElement(
        "p", {},
        "Edit",
        React.createElement(
          "code",
          {},
          "src/App.js"
        ),
        "and save to reload."
      ),
      React.createElement(
        "a",
        {
          className: "App-link",
          href: "https://reactjs.org",
          target: "_blank",
          rel: "noopener noreferrer"
        },
        "Learn React",
      ),
    )
  )
}

function tets() {
  return <div className="hello" data={1} >
    <span>go</span>
  </div>
}

function PAN2() {
  return <div>pan_1112</div>
}

